var app=new Vue({el:"#app",data:{apps:[],popupStatus:!1,appSelected:null},methods:{getAllApps:function(){fetch("https://superapps.fun/app/api.php?key=kpi47Gg45g45&event=allapps").then((p=>p.json())).then((p=>{this.apps=p}))},statusApp:function(){for(let p=0;p<this.apps.length;p++)switch(this.apps[p].myid=p,this.apps[p].bot){case"0":this.apps[p].text="Unwatch",this.apps[p].class="status_false";break;case"1":this.apps[p].text="Tune",this.apps[p].class="status_yellow";break;case"2":this.apps[p].text="Live";break;case"3":this.apps[p].text="FB Ban",this.apps[p].class="status_false";break;case"4":this.apps[p].text="Banned",this.apps[p].class="status_false";break;case"5":this.apps[p].text="Review",this.apps[p].class="status_yellow"}},popupMenu:function(p){document.querySelector(".app__list").classList.add("app__active"),this.appSelected=this.apps[p],this.popupStatus=!0},popupClose:function(){document.querySelector(".app__list").classList.remove("app__active"),this.appSelected=null,this.popupStatus=!1},sendApp:function(){(async()=>{const p=await fetch("https://superapps.fun/app/api.php?key=kpi47Gg45g45&event=setup",{method:"POST",headers:{Accept:"application/json","Content-Type":"application/json"},body:JSON.stringify(this.appSelected)}),t=await p.json();console.log(t)})(),document.querySelector(".app__list").classList.remove("app__active"),this.apps[this.appSelected.myid]=this.appSelected,this.appSelected=null,this.popupStatus=!1}}});